import { LoadingOutlined, PlusOutlined } from "@ant-design/icons";
import { Image, Upload } from "antd";
import React from "react";

const ColorSelected = ({
  colorSize,
  handleRemoveColor,
  getUploadProps,
  handleSizeClick,
  handleQuantityChange,
  loading,
}) => {
  return (
    <>
      {colorSize.map((color, index) => (
        <div
          key={index}
          className="w-full flex gap-10 items-start justify-between"
        >
          <div className="flex-1">
            <h5 className="text-base font-normal mb-2 mt-5">Màu sản phẩm</h5>
            <div className="color-size flex items-center gap-3">
              <button
                type="button"
                className={`w-14 h-14 flex items-center justify-center rounded-md shadow-lg hover:bg-blue-200 active:bg-blue-400`}
              >
                {color.color_name}
              </button>
              <button
                type="button"
                onClick={() => handleRemoveColor(color)}
                className={`w-14 h-14 flex items-center justify-center bg-red-300 rounded-md shadow-lg hover:bg-red-200 active:bg-red-400`}
              >
                Xoá
              </button>
              <Upload
                listType="picture-card"
                showUploadList={false}
                className="w-14 h-14"
                {...getUploadProps(null, `${color.color_name}`)}
              >
                <button className="w-14 !h-14" type="button">
                  {loading.null ? <LoadingOutlined /> : <PlusOutlined />}
                </button>
              </Upload>
            </div>
            <div className="mt-3">
              <Image
                width={56}
                height={56}
                src={colorSize[index]?.image}
                alt={`Image ${index + 1}`}
                fallback="data:image/png;base64,"
              />
            </div>
          </div>
          <div className="flex-1">
            <h5 className="text-base font-normal mb-2 mt-5">Size màu</h5>
            <div className="flex items-start gap-3">
              {["XS", "S", "M", "L", "XL", "2XL"].map((size, sizeIndex) => (
                <div key={sizeIndex} className="flex flex-col gap-3">
                  <button
                    type="button"
                    className={`w-14 h-14 border flex items-center justify-center rounded-md shadow-lg ${
                      colorSize[index].sizes?.some((item) => item.size === size)
                        ? "bg-blue-200"
                        : "bg-[#f4f5f6]"
                    } hover:bg-blue-200 active:bg-blue-400`}
                    onClick={() => handleSizeClick(index, size)}
                  >
                    {size}
                  </button>
                  {colorSize[index].sizes?.some(
                    (item) => item.size === size
                  ) && (
                    <div className="">
                      <input
                        type="number"
                        onChange={(e) =>
                          handleQuantityChange(
                            index,
                            size,
                            parseInt(e.target.value, 10)
                          )
                        }
                        className="w-14 h-14 text-center flex items-center justify-center bg-[#f4f5f6] rounded-md shadow-lg border border-blue-300"
                      />
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      ))}
    </>
  );
};

export default ColorSelected;
